import React from 'react';
import '../style/style.css';

class PageController extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: props.page, 
            prevEnabled: props.prevEnabled,
            nextEnabled: props.nextEnabled
        };

        this.goToPrevPage = this.goToPrevPage.bind(this);
        this.goToNextPage = this.goToNextPage.bind(this);
    }

    goToNextPage() {
        console.log('go to next page');
        this.props.onNext();
    }

    goToPrevPage() {
        this.props.onPrev();
    }

    prevButton() {
        return (
            <button className={`ui button ${this.state.prevEnabled == 'true'?'enabled':'disabled'} page-controller-button animated`} style={{float:"left"}} onClick={this.goToPrevPage}>
                <div className="hidden content">prev</div>
                <div className="visible content">
                    <i className="icon arrow left"></i>
                </div>
            </button>
        );
    }

    nextButton() {
        return (
            <button className={`ui button ${this.state.nextEnabled == 'true'?'enabled':'disabled'} page-controller-button animated`} style={{float: "right"}} onClick={this.goToNextPage}>
                <div className="hidden content">next</div>
                <div className="visible content">
                    <i className="icon arrow right"></i>
                </div>
            </button>
        );
    }

    render(){
        return (
            <div>
                {this.prevButton()}
                <span>current page : {this.state.page}</span>
                {this.nextButton()}
            </div>
        );
    }
}

export default PageController;
