import React from 'react';
import NoticeType from '../model/NoticeType';
import '../style/style.css';

class Notice extends React.Component {
    constructor(props) {
        super(props);
        this.state = {message: "", noticeType: NoticeType.BLUE};
        if(props.hasOwnProperty("message")) {
            this.setState({message: props.message});
        }
        if(props.hasOwnProperty("noticeType")) {
            this.setState({noticeType: props.noticeType});
        }
    }

    render() {
        return (
            <div className={`${this.state.noticeType}-notice notice`}>
                <div className="notice-message">{this.state.message}</div>
                <div className="breack-notice-line"></div>
                <div className="close-notice"></div>
            </div>
        );
    }
}

export default Notice;
