import React from 'react';
import '../style/style.css';

class PageController extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            page: props.page, 
            prevEnabled: props.prevEnabled,
            nextEnabled: props.nextEnabled
        };
    }

    prevButton() {
        return (
            <button className={`ui button ${this.state.prevEnabled == 'true'?'enabled-class':'disabled-class'} page-controller-button`} style={{float:"left"}}>
                <div className="hidden content">prev</div>
                <div className="visible content">
                    <i className="icon arrow left"></i>
                </div>
            </button>
        );
    }

    nextButton() {
        return (
            <button className={`ui button ${this.state.nextEnabled == 'true'?'enabled-class':'disabled-class'} page-controller-button animated vertical`} style={{float: "right"}}>
                <div className="hidden content">next</div>
                <div className="visible content">
                    <i className="icon arrow right"></i>
                </div>
            </button>
        );
    }

    render(){
        return (
            <div>
                {this.prevButton()}
                <span>current page : {this.state.page}</span>
                {this.nextButton()}
            </div>
        );
    }
}

export default PageController;
