import React from 'react';

class ImageCard extends React.Component {
    constructor(props) {
        super(props);
        this.postRef = React.createRef();
    }

    // componentDidMount() {
    //     this.postRef.current.addEventListener('load', this.setSpans);
    // }

    setSpans() {
        console.log(this.postRef);
    }

    render() {
        return(
            <div className="post" ref={this.postRef} onLoad={this.setSpans}>
                <img className="image" src={this.props.image.urls.full} key={this.props.key}/>
                <span className="img-alt">{this.props.image.alt_description}</span>
            </div>
        );
    }
}

export default ImageCard;
